/*ALTERANDO TABELAS*/CREATE TABLE TABELA(	COLUNA1 VARCHAR(30),	COLUNA2 VARCHAR(30),	COLUNA3 VARCHAR(30));--FARIAMOS ASSIM MAS CASO ESQUEÇA COMO NO CASO ACIMACREATE TABLE TABELA(	COLUNA1 INT PRIMARY KEY AUTO_COMPLETE,	COLUNA2 VARCHAR(30),	COLUNA3 VARCHAR(30));/* ADICIONANDO UM PK */ALTER TABLE TABELA ADD PRIMARY KEY (COLUNA1);--VERIFICA A DESCRICAO DA TABELADESC TABELA;/* obs	NÃO TENHO COMO COLOCAR O INCREMENT QUANDO COLOCAR A PRIMARY KEY POR FORA*/--ADICIONANDO COLUNA SEM POSICAO, QUANDO NAO DEFINIMOS A POSICAO --POR PADRAO FICA NA ULTIMA POSICAOALTER TABLE TABELAADD COLUNA VARCHAR(30);--ADICIONANDO UMA COLUNA COM POSICAOALTER TABLE TABELAADD COLUNA4 VARCHAR(30) NOT NULL UNIQUEAFTER COLUNA3;--MODIFICANDO O TIPO DE UM CAMPOALTER TABLE TABELA MODIFY COLUNA2 DATE NOT NULL;--RENOMEANDO TABELAALTER TABLE TABELA RENAME PESSOA;--- NAO PODEMOS MUDAR UM CHAR/VARCHAR PARA INTEIRO--- MAS PODEMOS MUDAR COLUNA DE NUMERO PARA INTEIROCREATE TABLE TIME(	IDTIME INT PRIMARY KEY AUTO_INCREMENT,	TIME VARCHAR(30),	ID_PESSOA VARCHAR(30)  );--CRIANDO UM FOREIGN KEY ALTER TABLE TIME ADD FOREIGN KEY (ID_PESSOA)REFERENCES PESSOA(COLUNA1);/*VERIFICAR AS CHAVES CRIADAS EM UMA TABELA*/SHOW CREATE TABLE TIME;/* ORGANIZACAO DE CHAVES - CONSTRAINT (REGRA)*/--INTEGRIDADE REFERENCIAL O BANCO NAO DEIXA A REFERENCIA FURADA;CREATE DATABASE FUTEBOL;CREATE TABLE JOGADOR(	IDJOGADOR INT PRIMARY KEY AUTO_INCREMENT,	NOME VARCHAR (30));CREATE TABLE TIME(	IDTIME INT PRIMARY KEY AUTO_INCREMENT,	NOMETIME VARCHAR(30),	ID_JOGADOR INT,	FOREIGN KEY(ID_JOGADOR)	REFERENCES JOGADOR(IDJOGADOR));INSERT INTO JOGADOR VALUES (NULL,'GUERREIRO');INSERT INTO TIME VALUES (NULL,'FLAMENGO',1);SELECT * FROM JOGADOR JINNER JOIN TIME T ON(T.ID_JOGADOR = J.IDJOGADOR);SHOW CREATE TABLE JOGADOR;SHOW CREATE TABLE TIME;/* A REGRA A SE SEGUIR E PRIMEIRO CRIAR AS TABELAS	DEPOIS CRIA AS CHAVES CONSTRAINTS	ASSIM E POSSIVEL ATRIBUIR NOME AS TABELAS*//*	DICIONARIO DE DADOS É MUITO IMPORTANTE PARA VERIFICAR AS TABELAS*//*ORGANIZANDO CHAVES E ACOES DE CONSTRAINTS*/---PRIMEIRO CRIAR A TABELA---DEPOIS AS CONSTRAINT--POIS ORGANIZA OS SCRIPTSCREATE TABLE CLIENT(	IDCLIENTE INT,	NOME VARCHAR(30) NOT NULL);CREATE TABLE TELEFONE(	IDTELEFONE INT,	TIPO CHAR(3) NOT NULL,	NUMERO VARCHAR(10) NOT NULL,	ID_CLIENTE INT);---OBS USANDO UNIQUE 1-1 --SEM UNIQUE 1-NALTER TABLE CLIENT ADD CONSTRAINT PK_CLIENTEPRIMARY KEY (IDCLIENTE);ALTER TABLE TELEFONE ADD CONSTRAINT FK_CLIENTE_TELEFONEFOREIGN KEY (ID_CLIENTE) REFERENCES CLIENT(IDCLIENTE);SHOW CREATE TABLE TELEFONE;/*DICIONARIO DE DADOS*/SHOW DATABASES;--PROCURANDO CONSTRAINTSUSE INFORMATION_SCHEMA;--VERIFICAR DATABASE EM USOSTATUSSHOW TABLES;DESC TABLE_CONSTRAINTS;SELECT CONSTRAINT_SCHEMA AS "BANCO",	   TABLE_NAME AS "TABELA",	   CONSTRAINT_NAME AS "NOME",	   CONSTRAINT_TYPE AS "TIPO"	   FROM TABLE_CONSTRAINTS	   WHERE CONSTRAINT_SCHEMA = 'futebol'; 	   /*APAGANDO CONSTRAINTS*/--VOCE SO CONSEGUE APAGAR NO BANCO DELEALTER TABLE TELEFONE DROP FOREIGN KEY FK_CLIENTE_TELEFONE;--INSERCAO EM MASSA---DROPA A FK E DEPOIS INSERE NOVAMENTE/* E ACOES DE CONSTRAINTS */